var app=angular.module("patronusApp",[]);app.controller("MainController",["$http",function(a){var b=this;b.peopleList=[],b.patList=[],b.getPats=function(){a.get("/patronus").then(function(a){b.patList=a.data})},b.getPeople=function(){a.get("/people").then(function(a){b.peopleList=a.data})},b.addPatronus=function(){a.post("/patronus",{name:b.patName}).then(b.getPats())},b.addPerson=function(){a.post("/people",{first_name:b.personFirst,last_name:b.personLast}).then(b.getPeople())},b.getPats(),b.getPeople()}]);